/**
 * The sidebar containing the main widget area.
 *
 * If no active widgets in sidebar, let's hide it completely.
 *
 * @package WordPress
 * @subpackage Fresh_Sky
 * @since Fresh Sky 1.0
 */
global $h_option, $custom_widget_area;
$default_sidebar = true;

	echo '<div id="secondary" class="widget-area" role="complementary">';

	//unique Page sidebars:
	if (function_exists('dynamic_sidebar') && dynamic_sidebar('Page: '.$custom_widget_area) ) : $default_sidebar = false; endif;
	
	//unique Category sidebars
	if (function_exists('dynamic_sidebar') && dynamic_sidebar('Category: '.$custom_widget_area) ) : $default_sidebar = false;endif;

	//页面
	// if ($h_option['showSidebar'] == 'page' && is_active_sidebar( 'sidebar-page' ) ):
	// 	$default_sidebar = false; dynamic_sidebar('sidebar-page');endif;
	
	 if ($h_option['showSidebar'] == 'page' && dynamic_sidebar('sidebar-page') ):
	 	$default_sidebar = false; endif;

	//分类页
	if ($default_sidebar && $h_option['showSidebar'] == 'category' && is_active_sidebar( 'sidebar-category' ) ):
		$default_sidebar = false; dynamic_sidebar('sidebar-category');endif;

	//标签页
	if ($default_sidebar && $h_option['showSidebar'] == 'tag' && is_active_sidebar( 'sidebar-tag' ) ):
		$default_sidebar = false; dynamic_sidebar('sidebar-tag');endif;

	//文章页
	if ($h_option['showSidebar'] == 'single' && is_active_sidebar( 'sidebar-single' ) ):
		$default_sidebar = false; dynamic_sidebar('sidebar-single');endif;



	//default dummy sidebar 默认的侧边栏 分为page页面和普通页面
	if ($default_sidebar && $h_option['includes']['dummy_sidebars'] == 1){
		dynamic_sidebar( 'sidebar-1' ); 
	}else{
		//echo "string";
	}
					
					





	echo '</div>';

